function filterCall(idss){     
    var getpara_tags=$('.tagchk:checked').map(function() {return this.value;}).get().join(',');
    var getpara_themes=$('.themechk:checked').map(function() {return this.value;}).get().join(',');
    var getpara_country=$('.countrychk:checked').map(function() {return this.value;}).get().join(',');
	var getordertags="?tags=";
	var getorderthemes="?themes=";
	var getordercountry="?country=";	

    if (getpara_tags!="" && getpara_themes!="" && getpara_country!=""){
        var addpara=getordertags+getpara_tags+"&themes="+getpara_themes+"&country="+getpara_country;
    }else if (getpara_tags!="" && getpara_themes!=""){
        var addpara=getordertags+getpara_tags+"&themes="+getpara_themes;
    }else if (getpara_themes!="" && getpara_country!=""){
        var addpara=getorderthemes+getpara_themes+"&country="+getpara_country;
    }else if (getpara_tags!="" && getpara_country!=""){
        var addpara=getordertags+getpara_tags+"&country="+getpara_country;
    }else if (getpara_tags!="") {
        var addpara=getordertags+getpara_tags;
    }else if(getpara_themes!=""){
        var addpara=getorderthemes+getpara_themes;
    }else if(getpara_country!=""){
        var addpara=getordercountry+getpara_country;
    }else{
        var addpara="";
    }
    if(idss == 2){
        var geturl = window.location.href;			
        var pagepara = "page=";

        var urlfortagstr = "";
        var urlfortagres = urlfortagstr.replace("&amp;", "&");
		var addpararep = addpara.replace("&amp;", "&");

        if (geturl.match(pagepara)) {			
			if (addpararep!="") {			  
				if(geturl!=WS_PATH+urlfortagres+addpararep){  
					window.location.href = WS_PATH+urlfortagres+addpararep;
				}   
			}else{
				if ((geturl.match("tags=") || geturl.match("themes=") || geturl.match("country=")) && geturl.match(pagepara)) {				
					if(geturl!=WS_PATH+urlfortagres+addpararep){  
						window.location.href = WS_PATH+urlfortagres+addpararep;
					} 
				}else{				  
				}
			}
		}else{
			if ((geturl.match("by_alphabetical=") || geturl.match("by_date=")) && addpararep=="") {
				if(geturl!=WS_PATH+urlfortagres+addpararep){
					window.location.href = WS_PATH+urlfortagres+addpararep;	
				}	
			}else{
				if(geturl!=WS_PATH+urlfortagres+addpararep){  
					window.location.href = WS_PATH+urlfortagres+addpararep;
				} 	
			}			
		}					
    }else{
    }     
}
